using ShineEngine;

/// <summary>
/// 单位显示部件表(generated by shine)
/// </summary>
public class AvatarPartConfig:BaseConfig
{
	/** 存储集合 */
	private static LongObjectMap<AvatarPartConfig> _dic;
	
	/// <summary>
	/// 部件类型
	/// </summary>
	public int type;
	
	/// <summary>
	/// 部件ID
	/// </summary>
	public int id;
	
	/// <summary>
	/// 资源
	/// </summary>
	public string source;
	
	/// <summary>
	/// 资源(资源转)
	/// </summary>
	public int sourceT;
	
	/// <summary>
	/// 优先级
	/// </summary>
	public int proirity;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static AvatarPartConfig get(int type,int id)
	{
		return _dic.get((long)type << 32 | (long)id);
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(LongObjectMap<AvatarPartConfig> dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(LongObjectMap<AvatarPartConfig> dic)
	{
		_dic.putAll(dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static LongObjectMap<AvatarPartConfig> getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.type=stream.readInt();
		
		this.id=stream.readInt();
		
		this.source=stream.readUTF();
		
		this.proirity=stream.readInt();
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
	/// <summary>
	/// 生成刷新配置
	/// </summary>
	protected override void generateRefresh()
	{
		sourceT=LoadControl.getResourceIDByName(source);
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.type);
		
		stream.writeInt(this.id);
		
		stream.writeUTF(this.source);
		
		stream.writeInt(this.proirity);
		
	}
	
}
