using ShineEngine;

/// <summary>
/// 兑换表(generated by shine)
/// </summary>
public class ExchangeConfig:BaseConfig
{
	/** 存储集合 */
	private static IntObjectMap<ExchangeConfig> _dic;
	
	/// <summary>
	/// 组id
	/// </summary>
	public int groupID;
	
	/// <summary>
	/// id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 消耗id
	/// </summary>
	public int costID;
	
	/// <summary>
	/// 奖励id
	/// </summary>
	public int rewarID;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static ExchangeConfig get(int id)
	{
		return _dic.get(id);
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(IntObjectMap<ExchangeConfig> dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(IntObjectMap<ExchangeConfig> dic)
	{
		_dic.putAll(dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static IntObjectMap<ExchangeConfig> getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.groupID=stream.readInt();
		
		this.costID=stream.readInt();
		
		this.rewarID=stream.readInt();
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.groupID);
		
		stream.writeInt(this.costID);
		
		stream.writeInt(this.rewarID);
		
	}
	
}
