using ShineEngine;

/// <summary>
/// 字体表(程序用)(需国际化)(generated by shine)
/// </summary>
public class FontConfig:BaseConfig
{
	/** 存储集合 */
	private static FontConfig[] _dic;
	
	/// <summary>
	/// 名字
	/// </summary>
	public string name;
	
	/// <summary>
	/// id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 简体中文
	/// </summary>
	public string zh_CN;
	
	/// <summary>
	/// 美国英语
	/// </summary>
	public string en_US;
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static FontConfig[] getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.name=stream.readUTF();
		
		this.zh_CN=stream.readUTF();
		
		this.en_US=stream.readUTF();
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
	protected virtual string getStr(int type)
	{
		switch(type)
		{
			case LanguageType.Zh_CN:
				return zh_CN;
			case LanguageType.En_US:
				return en_US;
		}

		return null;
	}
	
	public static string getFontSource(int key)
	{
		FontConfig config;
		string re;

		if((config=get(key))!=null && (re=config.getStr(CommonSetting.languageType))!=null)
			return re;

		Ctrl.throwError("找不到TextConfig",key);

		return null;
	}
	
	/// <summary>
	/// 获取
	/// </summary>
	public static FontConfig get(int id)
	{
		return id>=0 && id<_dic.Length ? _dic[id] : null;
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(FontConfig[] dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(FontConfig[] dic)
	{
		ObjectUtils.arrayPutAll(_dic,dic);
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeUTF(this.name);
		
		stream.writeUTF(this.zh_CN);
		
		stream.writeUTF(this.en_US);
		
	}
	
}
