using ShineEngine;

/// <summary>
/// 国际化表(generated by shine)
/// </summary>
public class LanguageConfig:BaseConfig
{
	/** 存储集合 */
	private static SMap<string,LanguageConfig> _dic;
	
	/// <summary>
	/// 美国英语
	/// </summary>
	public string en_US;
	
	/// <summary>
	/// 简体中文
	/// </summary>
	public string zh_CN;
	
	/// <summary>
	/// 键
	/// </summary>
	public string key;
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static SMap<string,LanguageConfig> getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.key=stream.readUTF();
		
		this.zh_CN=stream.readUTF();
		
		this.en_US=stream.readUTF();
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
	protected virtual string getStr(int type)
	{
		switch(type)
		{
			case LanguageType.Zh_CN:
				return zh_CN;
			case LanguageType.En_US:
				return en_US;
		}

		return null;
	}
	
	public static string getText(string key)
	{
		if(key.isEmpty())
			return key;

		LanguageConfig config;
		string re;

		if((config=get(key))!=null && (re=config.getStr(CommonSetting.languageType))!=null)
			return re;

		if(CommonSetting.useMultiLanguage)
		{
			Ctrl.throwError("找不到LanguageConfig",key);
		}

		return key;
	}
	
	/// <summary>
	/// 获取文字
	/// </summary>
	public static string[] getTexts(string[] keys)
	{
		string[] re=new string[keys.Length];

		for(int i=0,len=keys.Length;i<len;++i)
		{
			re[i]=getText(keys[i]);
		}

		return re;
	}
	
	/// <summary>
	/// 获取
	/// </summary>
	public static LanguageConfig get(string key)
	{
		return _dic.get(key);
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(SMap<string,LanguageConfig> dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(SMap<string,LanguageConfig> dic)
	{
		_dic.putAll(dic);
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeUTF(this.key);
		
		stream.writeUTF(this.zh_CN);
		
		stream.writeUTF(this.en_US);
		
	}
	
}
