using ShineEngine;

/// <summary>
/// 加载资源表(generated by shine)
/// </summary>
public class MarkResourceConfig:BaseConfig
{
	/** 存储集合 */
	private static SMap<string,MarkResourceConfig> _dic;
	
	/// <summary>
	/// 资源路径
	/// </summary>
	public string url;
	
	/// <summary>
	/// 类型
	/// </summary>
	public int type;
	
	/// <summary>
	/// 初次加载组
	/// </summary>
	public static IntList firstLoadList;
	
	/// <summary>
	/// 初次场景加载组
	/// </summary>
	public static IntList firstSceneLoadList;
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static SMap<string,MarkResourceConfig> getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.url=stream.readUTF();
		
		this.type=stream.readInt();
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		firstLoadList=new IntList();
		firstSceneLoadList=new IntList();

		_dic.forEachValue(v=>
		{
			int resourceID=LoadControl.getResourceIDByName(BaseGameUtils.getURLReplace(v.url));

			switch(v.type)
			{
				case MarkResourceType.FirstLoad:
				{
					firstLoadList.add(resourceID);
				}
					break;
				case MarkResourceType.FirstSceneLoad:
				{
					firstSceneLoadList.add(resourceID);
				}
					break;
			}
		});
	}
	
	/// <summary>
	/// 获取
	/// </summary>
	public static MarkResourceConfig get(string url)
	{
		return _dic.get(url);
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(SMap<string,MarkResourceConfig> dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(SMap<string,MarkResourceConfig> dic)
	{
		_dic.putAll(dic);
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeUTF(this.url);
		
		stream.writeInt(this.type);
		
	}
	
}
