using ShineEngine;

/// <summary>
/// 模型动作类型(generated by shine)
/// </summary>
public class MotionConfig:BaseConfig
{
	/** 存储集合 */
	private static MotionConfig[] _dic;
	
	/// <summary>
	/// id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 名字
	/// </summary>
	public string name;
	
	/// <summary>
	/// 使用名字
	/// </summary>
	public string useName;
	
	/// <summary>
	/// 描述
	/// </summary>
	public string describe;
	
	/// <summary>
	/// 默认持续时间(ms)
	/// </summary>
	public int defaultMotionTime;
	
	/// <summary>
	/// 找不到动作的转化ID
	/// </summary>
	public int backID;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static MotionConfig get(int id)
	{
		return id>=0 && id<_dic.Length ? _dic[id] : null;
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(MotionConfig[] dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(MotionConfig[] dic)
	{
		ObjectUtils.arrayPutAll(_dic,dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static MotionConfig[] getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.name=stream.readUTF();
		
		this.describe=stream.readUTF();
		
		this.useName=stream.readUTF();
		
		this.backID=stream.readInt();
		
		this.defaultMotionTime=stream.readInt();
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeUTF(this.name);
		
		stream.writeUTF(this.describe);
		
		stream.writeUTF(this.useName);
		
		stream.writeInt(this.backID);
		
		stream.writeInt(this.defaultMotionTime);
		
	}
	
}
