using ShineEngine;

/// <summary>
/// 平台表(generated by shine)
/// </summary>
public class PlatformConfig:BaseConfig
{
	/** 存储集合 */
	private static SMap<string,PlatformConfig> _dic;
	
	/// <summary>
	/// id
	/// </summary>
	public string id;
	
	/// <summary>
	/// 充值地址
	/// </summary>
	public string chargeURL;
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static SMap<string,PlatformConfig> getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readUTF();
		
		this.chargeURL=stream.readUTF();
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
	/// <summary>
	/// 获取
	/// </summary>
	public static PlatformConfig get(string id)
	{
		return _dic.get(id);
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(SMap<string,PlatformConfig> dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(SMap<string,PlatformConfig> dic)
	{
		_dic.putAll(dic);
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeUTF(this.id);
		
		stream.writeUTF(this.chargeURL);
		
	}
	
}
