using ShineEngine;

/// <summary>
/// 玩家群等级表(generated by shine)
/// </summary>
public class RoleGroupLevelConfig:BaseConfig
{
	/** 存储集合 */
	private static LongObjectMap<RoleGroupLevelConfig> _dic;
	
	/// <summary>
	/// id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 等级
	/// </summary>
	public int level;
	
	/// <summary>
	/// 经验上限
	/// </summary>
	public long expMax;
	
	/// <summary>
	/// 最多人数
	/// </summary>
	public int maxNum;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static RoleGroupLevelConfig get(int id,int level)
	{
		return _dic.get((long)id << 32 | (long)level);
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(LongObjectMap<RoleGroupLevelConfig> dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(LongObjectMap<RoleGroupLevelConfig> dic)
	{
		_dic.putAll(dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static LongObjectMap<RoleGroupLevelConfig> getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.level=stream.readInt();
		
		this.maxNum=stream.readInt();
		
		this.expMax=stream.readLong();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.level);
		
		stream.writeInt(this.maxNum);
		
		stream.writeLong(this.expMax);
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
}
