using ShineEngine;

/// <summary>
/// 场景角色属性类型表(generated by shine)
/// </summary>
public class SceneRoleAttributeConfig:BaseConfig
{
	/** 存储集合 */
	private static SceneRoleAttributeConfig[] _dic;
	
	/// <summary>
	/// 属性id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 名字
	/// </summary>
	public string name;
	
	/// <summary>
	/// 显示名
	/// </summary>
	public string showName;
	
	/// <summary>
	/// 方式
	/// </summary>
	public int method;
	
	/// <summary>
	/// 推送角色自己方式
	/// </summary>
	public int sendSelfType;
	
	/// <summary>
	/// 当前属性默认空满
	/// </summary>
	public bool isCurrentDefaultFull;
	
	/** 显示名原值 */
	private string _showName;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static SceneRoleAttributeConfig get(int id)
	{
		return id>=0 && id<_dic.Length ? _dic[id] : null;
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(SceneRoleAttributeConfig[] dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(SceneRoleAttributeConfig[] dic)
	{
		ObjectUtils.arrayPutAll(_dic,dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static SceneRoleAttributeConfig[] getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.name=stream.readUTF();
		
		this.showName=stream.readUTF();
		
		this.method=stream.readInt();
		
		this.sendSelfType=stream.readInt();
		
		this.isCurrentDefaultFull=stream.readBoolean();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeUTF(this.name);
		
		stream.writeUTF(this.showName);
		
		stream.writeInt(this.method);
		
		stream.writeInt(this.sendSelfType);
		
		stream.writeBoolean(this.isCurrentDefaultFull);
		
	}
	
	/// <summary>
	/// 生成刷新配置
	/// </summary>
	protected override void generateRefresh()
	{
		if(_showName==null)
			_showName=showName;
		showName=LanguageConfig.getText(_showName);
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
}
