using ShineEngine;

/// <summary>
/// 技能读条表(generated by shine)
/// </summary>
public class SkillBarConfig:BaseConfig
{
	/** 存储集合 */
	private static IntObjectMap<SkillBarConfig> _dic;
	
	/// <summary>
	/// 读条id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 读条类型
	/// </summary>
	public int type;
	
	/// <summary>
	/// 动作id
	/// </summary>
	public int motionID;
	
	/// <summary>
	/// 读条时间(ms)
	/// </summary>
	public int time;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static SkillBarConfig get(int id)
	{
		return _dic.get(id);
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(IntObjectMap<SkillBarConfig> dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(IntObjectMap<SkillBarConfig> dic)
	{
		_dic.putAll(dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static IntObjectMap<SkillBarConfig> getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.type=stream.readInt();
		
		this.motionID=stream.readInt();
		
		this.time=stream.readInt();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.type);
		
		stream.writeInt(this.motionID);
		
		stream.writeInt(this.time);
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
}
