using ShineEngine;

/// <summary>
/// 技能几率表(generated by shine)
/// </summary>
public class SkillProbConfig:BaseConfig
{
	/** 存储集合 */
	private static IntObjectMap<SkillProbConfig> _dic;
	
	/// <summary>
	/// 几率id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 几率值(千分位)
	/// </summary>
	public int value;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static SkillProbConfig get(int id)
	{
		return _dic.get(id);
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(IntObjectMap<SkillProbConfig> dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(IntObjectMap<SkillProbConfig> dic)
	{
		_dic.putAll(dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static IntObjectMap<SkillProbConfig> getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.value=stream.readInt();
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.value);
		
	}
	
}
