using ShineEngine;

/// <summary>
/// 队伍目标表(generated by shine)
/// </summary>
public class TeamTargetConfig:BaseConfig
{
	/** 存储集合 */
	private static TeamTargetConfig[] _dic;
	
	/// <summary>
	/// 需要等级
	/// </summary>
	public int needLevel;
	
	/// <summary>
	/// id
	/// </summary>
	public int id;
	
	/** 描述原值 */
	private string _name;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static TeamTargetConfig get(int id)
	{
		return id>=0 && id<_dic.Length ? _dic[id] : null;
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(TeamTargetConfig[] dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(TeamTargetConfig[] dic)
	{
		ObjectUtils.arrayPutAll(_dic,dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static TeamTargetConfig[] getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.needLevel=stream.readInt();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.needLevel);
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
}
