using ShineEngine;

/// <summary>
/// 拍卖行查询条件类型(generated by shine)
/// </summary>
public class AuctionQueryConditionTypeConfig:BaseConfig
{
	/** 存储集合 */
	private static AuctionQueryConditionTypeConfig[] _dic;
	
	/// <summary>
	/// 是否降序
	/// </summary>
	public bool isDesc;
	
	/// <summary>
	/// 比较方式
	/// </summary>
	public int compareType;
	
	/// <summary>
	/// id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static AuctionQueryConditionTypeConfig get(int id)
	{
		return id>=0 && id<_dic.Length ? _dic[id] : null;
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(AuctionQueryConditionTypeConfig[] dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(AuctionQueryConditionTypeConfig[] dic)
	{
		ObjectUtils.arrayPutAll(_dic,dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static AuctionQueryConditionTypeConfig[] getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.compareType=stream.readInt();
		
		this.isDesc=stream.readBoolean();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.compareType);
		
		stream.writeBoolean(this.isDesc);
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
}
