using ShineEngine;

/// <summary>
/// 客户端平台类型(generated by shine)
/// </summary>
public class ClientPlatformTypeConfig:BaseConfig
{
	/** 存储集合 */
	private static ClientPlatformTypeConfig[] _dic;
	
	/// <summary>
	/// id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 名字
	/// </summary>
	public string name;
	
	private static StringIntMap _nameDic;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static ClientPlatformTypeConfig get(int id)
	{
		return id>=0 && id<_dic.Length ? _dic[id] : null;
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(ClientPlatformTypeConfig[] dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(ClientPlatformTypeConfig[] dic)
	{
		ObjectUtils.arrayPutAll(_dic,dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static ClientPlatformTypeConfig[] getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.name=stream.readUTF();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeUTF(this.name);
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		_nameDic=new StringIntMap();
		foreach(ClientPlatformTypeConfig v in _dic)
		{
			if(v!=null)
			{
				_nameDic.put(v.name,v.id);
			}
		}
	}
	
	/// <summary>
	/// 通过名字获取
	/// </summary>
	public static int getByName(string name)
	{
		return _nameDic.get(name);
	}
	
}
