using ShineEngine;

/// <summary>
/// 推送标签类型(generated by shine)
/// </summary>
public class PushTopicTypeConfig:BaseConfig
{
	/** 存储集合 */
	private static PushTopicTypeConfig[] _dic;
	
	/// <summary>
	/// id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 标签名
	/// </summary>
	public int type;
	
	/// <summary>
	/// 参数
	/// </summary>
	public int[] args;
	
	/// <summary>
	/// 标签名
	/// </summary>
	public string topic;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static PushTopicTypeConfig get(int id)
	{
		return id>=0 && id<_dic.Length ? _dic[id] : null;
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(PushTopicTypeConfig[] dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(PushTopicTypeConfig[] dic)
	{
		ObjectUtils.arrayPutAll(_dic,dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static PushTopicTypeConfig[] getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.topic=stream.readUTF();
		
		this.type=stream.readInt();
		
		int argsLen=stream.readLen();
		if(this.args==null || this.args.Length!=argsLen)
		{
			this.args=new int[argsLen];
		}
		int[] argsT=this.args;
		for(int argsI=0;argsI<argsLen;++argsI)
		{
			int argsV;
			argsV=stream.readInt();
			
			argsT[argsI]=argsV;
		}
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeUTF(this.topic);
		
		stream.writeInt(this.type);
		
		if(this.args!=null)
		{
			int[] argsT=this.args;
			stream.writeLen(argsT.Length);
			for(int argsVI=0,argsVLen=argsT.Length;argsVI<argsVLen;++argsVI)
			{
				int argsV=argsT[argsVI];
				stream.writeInt(argsV);
				
			}
		}
		else
		{
			nullObjError("args");
		}
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
}
