using ShineEngine;

/// <summary>
/// 单位移动方式(generated by shine)
/// </summary>
public class UnitMoveTypeConfig:BaseConfig
{
	/** 存储集合 */
	private static UnitMoveTypeConfig[] _dic;
	
	/// <summary>
	/// id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 使用速度比率
	/// </summary>
	public float radio;
	
	/// <summary>
	/// 动作id
	/// </summary>
	public int motionID;
	
	/// <summary>
	/// 获取
	/// </summary>
	public static UnitMoveTypeConfig get(int id)
	{
		return id>=0 && id<_dic.Length ? _dic[id] : null;
	}
	
	/// <summary>
	/// 设置字典
	/// </summary>
	public static void setDic(UnitMoveTypeConfig[] dic)
	{
		_dic=dic;
	}
	
	/// <summary>
	/// 添加字典(热更用)
	/// </summary>
	public static void addDic(UnitMoveTypeConfig[] dic)
	{
		ObjectUtils.arrayPutAll(_dic,dic);
	}
	
	/// <summary>
	/// 获取全部
	/// </summary>
	public static UnitMoveTypeConfig[] getDic()
	{
		return _dic;
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.radio=stream.readFloat();
		
		this.motionID=stream.readInt();
		
	}
	
	/// <summary>
	/// 读完所有表后处理
	/// </summary>
	public static void afterReadConfigAll()
	{
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeFloat(this.radio);
		
		stream.writeInt(this.motionID);
		
	}
	
}
