using ShineEngine;

/// <summary>
/// 攻击范围类型(generated by shine)
/// </summary>
public class AttackScopeType
{
	/// <summary>
	/// 无目标
	/// </summary>
	public const int None=0;
	
	/// <summary>
	/// 自身
	/// </summary>
	public const int Self=1;
	
	/// <summary>
	/// 单体
	/// </summary>
	public const int Single=2;
	
	/// <summary>
	/// 2D攻击矩形(act用)(arg0-3:x,y,width,height)
	/// </summary>
	public const int AttackRect=3;
	
	/// <summary>
	/// 自身圆形范围(arg0:半径,[arg1:高度])
	/// </summary>
	public const int SelfCircle=11;
	
	/// <summary>
	/// 目标圆形范围(arg0:半径,[arg1:高度])
	/// </summary>
	public const int TargetCircle=12;
	
	/// <summary>
	/// 自身矩形范围(arg0:长(纵向),arg1:宽(横向),[arg2:高度])
	/// </summary>
	public const int SelfRect=13;
	
	/// <summary>
	/// 自身扇形范围(arg0:半径,arg1:扇角,[arg2:高度])
	/// </summary>
	public const int SelfSector=14;
	
	/// <summary>
	/// 自身圆形范围内最近的几个目标(arg0:半径,arg1:目标数,[arg2:高度])
	/// </summary>
	public const int SelfCircleNear=15;
	
	/// <summary>
	/// 自身圆形范围内随机的几个目标(arg0:半径,arg1:目标数,[arg2:高度])
	/// </summary>
	public const int SelfCircleRandom=16;
	
	/// <summary>
	/// 自身圆形范围内最近的一个目标(arg0:半径,[arg1:高度])
	/// </summary>
	public const int SelfNearest=17;
	
	/// <summary>
	/// 2D周身(act用)(arg0:半径,arg1:高度)
	/// </summary>
	public const int Around2D=21;
	
	/// <summary>
	/// 长度
	/// </summary>
	public static int size=22;
	
}
