using ShineEngine;

/// <summary>
/// 副本状态类型(generated by shine)
/// </summary>
public class BattleStateType
{
	/// <summary>
	/// 等待中(一个人都没有)
	/// </summary>
	public const int Wait=1;
	
	/// <summary>
	/// 进入阶段(进入第一人到倒计时结束)
	/// </summary>
	public const int Enter=2;
	
	/// <summary>
	/// 预备(321倒计时)
	/// </summary>
	public const int Pre=3;
	
	/// <summary>
	/// 运行中
	/// </summary>
	public const int Running=4;
	
	/// <summary>
	/// 已完成(完成到结算倒计时)
	/// </summary>
	public const int Over=5;
	
	/// <summary>
	/// 已结算(结算到清场倒计时)
	/// </summary>
	public const int Result=6;
	
	/// <summary>
	/// 已清场(待销毁)
	/// </summary>
	public const int End=7;
	
	/// <summary>
	/// 长度
	/// </summary>
	public static int size=8;
	
}
