using ShineEngine;
using UnityEngine;

/// <summary>
/// 客户端平台类型(generated by shine)
/// </summary>
public class ClientPlatformType
{
	public const int Windows=1;
	
	public const int Mac=2;
	
	public const int Android=3;
	
	public const int Ios=4;
	
	/// <summary>
	/// 长度
	/// </summary>
	public static int size=5;
	
	/// <summary>
	/// 获取平台名
	/// </summary>
	public static string getName(int type)
	{
		return ClientPlatformTypeConfig.get(type).name;
	}
	
	/// <summary>
	/// 获取类型
	/// </summary>
	public static int getTypeByRuntime(RuntimePlatform platform)
	{
		switch(platform)
		{
			case RuntimePlatform.WindowsEditor:
			case RuntimePlatform.WindowsPlayer:
			{
				return ClientPlatformType.Windows;
			}
			case RuntimePlatform.OSXEditor:
			case RuntimePlatform.OSXPlayer:
			{
				return ClientPlatformType.Mac;
			}
			case RuntimePlatform.Android:
			{
				return ClientPlatformType.Android;
			}
			case RuntimePlatform.IPhonePlayer:
			{
				return ClientPlatformType.Ios;
			}
		}

		return -1;
	}
	
}
