using ShineEngine;

/// <summary>
/// 任务提交类型(generated by shine)
/// </summary>
public class QuestCommitType
{
	/// <summary>
	/// 不可主动提交
	/// </summary>
	public const int Passive=0;
	
	/// <summary>
	/// 找npc提交
	/// </summary>
	public const int ByNPC=1;
	
	/// <summary>
	/// 自动提交(客户端)(如包满会暂停流程)
	/// </summary>
	public const int Auto=2;
	
	/// <summary>
	/// 自动提交(服务器)(包满邮件)
	/// </summary>
	public const int ServerAuto=3;
	
	/// <summary>
	/// 客户端提交
	/// </summary>
	public const int Client=4;
	
	/// <summary>
	/// 长度
	/// </summary>
	public static int size=5;
	
}
