using ShineEngine;

/// <summary>
/// 随机物品组方式(generated by shine)
/// </summary>
public class RandomItemListType
{
	/// <summary>
	/// 单个物品(id,[num])
	/// </summary>
	public const int SingleItem=1;
	
	/// <summary>
	/// 单个随机物品(randomItemID,[num])
	/// </summary>
	public const int SingleRandomItem=2;
	
	/// <summary>
	/// 几率(万分位)随机物品组求和(prob,randomID,[min],[max];prob,randomID,[min],[max])
	/// </summary>
	public const int SumProbRandomItem=3;
	
	/// <summary>
	/// 权重随机list(嵌套)(weight,randomListID;weight,randomListID)
	/// </summary>
	public const int WeightRandomList=4;
	
	/// <summary>
	/// 长度
	/// </summary>
	public static int size=5;
	
}
