using ShineEngine;

/// <summary>
/// 随机物品方式(generated by shine)
/// </summary>
public class RandomItemType
{
	/// <summary>
	/// 单个物品(id,[num])
	/// </summary>
	public const int Single=1;
	
	/// <summary>
	/// 等权物品组(id,[num];id,[num])
	/// </summary>
	public const int AverageItem=2;
	
	/// <summary>
	/// 权重随机物品组(weight,id,[num];weight,id,[num],id,[num])
	/// </summary>
	public const int WeightItem=3;
	
	/// <summary>
	/// 权重随机创建物品组(weight,createID,[num];weight,createID,[num])
	/// </summary>
	public const int WeightCreateItem=4;
	
	/// <summary>
	/// 权重随机随机物品组(嵌套)(weight,randomItemID;weight,randomItemID)
	/// </summary>
	public const int WeightRandomItem=5;
	
	/// <summary>
	/// 长度
	/// </summary>
	public static int size=6;
	
}
