using ShineEngine;

/// <summary>
/// 技能影响类型(generated by shine)
/// </summary>
public class SkillInfluenceType
{
	/// <summary>
	/// 标准敌对
	/// </summary>
	public static bool[] enemyE;
	
	/// <summary>
	/// 标准友方
	/// </summary>
	public static bool[] friendE;
	
	/// <summary>
	/// 是否影响敌方
	/// </summary>
	public const int Enemy=1;
	
	/// <summary>
	/// 是否影响友方(不包括自己)
	/// </summary>
	public const int Friend=2;
	
	/// <summary>
	/// 是否影响自己
	/// </summary>
	public const int Self=3;
	
	/// <summary>
	/// 是否影响已死单位
	/// </summary>
	public const int Death=4;
	
	/// <summary>
	/// 是否影响角色
	/// </summary>
	public const int Character=5;
	
	/// <summary>
	/// 是否影响怪物
	/// </summary>
	public const int Monster=6;
	
	/// <summary>
	/// 是否影响掉落物品
	/// </summary>
	public const int FieldItem=7;
	
	/// <summary>
	/// 是否影响建筑
	/// </summary>
	public const int Building=8;
	
	/// <summary>
	/// 是否影响操作体
	/// </summary>
	public const int Operation=9;
	
	/// <summary>
	/// 是否影响中立方
	/// </summary>
	public const int Nuetral=10;
	
	/// <summary>
	/// 是否跳过技能免疫单位
	/// </summary>
	public const int NeedSkillImmun=11;
	
	/// <summary>
	/// 长度
	/// </summary>
	public static int size=12;
	
}
