using ShineEngine;

/// <summary>
/// 任务目标类型(generated by shine)
/// </summary>
public class TaskType
{
	/// <summary>
	/// npc对话(arg1:npcID)
	/// </summary>
	public const int NpcDialog=1;
	
	/// <summary>
	/// 通关副本(arg1:battleID)
	/// </summary>
	public const int WinBattle=2;
	
	/// <summary>
	/// 击杀怪物(arg1:monsterID)
	/// </summary>
	public const int KillMonster=3;
	
	/// <summary>
	/// 玩家等级到达
	/// </summary>
	public const int Level=4;
	
	/// <summary>
	/// 好友数量到达
	/// </summary>
	public const int FriendNum=5;
	
	/// <summary>
	/// 累计获得货币(arg1:currencyType)
	/// </summary>
	public const int TotalAddCurrency=6;
	
	/// <summary>
	/// 累计消耗货币(arg1:currencyType)
	/// </summary>
	public const int TotalCostCurrency=7;
	
	/// <summary>
	/// 拥有物品(arg1:itemId, arg2:itemNum)
	/// </summary>
	public const int HasItem=8;
	
	/// <summary>
	/// 击杀怪物几率获得计数(arg1:monsterID,arg2:几率千分位)
	/// </summary>
	public const int KillMonsterGetNum=9;
	
	/// <summary>
	/// 击杀怪物几率获得物品(实体)(arg1:monsterID,arg2:itemID,arg3:几率千分位)
	/// </summary>
	public const int KillMonsterGetItem=10;
	
	/// <summary>
	/// 进入区域(arg1:sceneID,arg2:regionInstanceID)(需要对应区域上标记needTaskEvent)
	/// </summary>
	public const int EnterRegion=11;
	
	/// <summary>
	/// 长度
	/// </summary>
	public static int size=12;
	
}
