using ShineEngine;

/// <summary>
/// 程序文本枚举(generated by shine)
/// </summary>
public class TextEnum
{
	/// <summary>
	/// 网络未开启
	/// </summary>
	public static string NetNotOpen;
	
	/// <summary>
	/// 服务器未响应
	/// </summary>
	public static string ServerNotRespond;
	
	/// <summary>
	/// 网络连接被断开
	/// </summary>
	public static string SocketClosed;
	
	/// <summary>
	/// 需要下载新的更新包
	/// </summary>
	public static string NeedHotfix;
	
	/// <summary>
	/// 从配置读取
	/// </summary>
	public static void readConfig()
	{
		NetNotOpen=TextConfig.getText("netNotOpen");
		ServerNotRespond=TextConfig.getText("serverNotRespond");
		SocketClosed=TextConfig.getText("socketClosed");
		NeedHotfix=TextConfig.getText("needHotfix");
	}
	
}
