using ShineEngine;

/// <summary>
/// 单位类型(generated by shine)
/// </summary>
public class UnitType
{
	/// <summary>
	/// 角色
	/// </summary>
	public const int Character=1;
	
	/// <summary>
	/// 怪物
	/// </summary>
	public const int Monster=2;
	
	/// <summary>
	/// NPC
	/// </summary>
	public const int Npc=3;
	
	/// <summary>
	/// 宠物
	/// </summary>
	public const int Pet=4;
	
	/// <summary>
	/// 傀儡(辅助施法单位)(召唤单位)
	/// </summary>
	public const int Puppet=5;
	
	/// <summary>
	/// 载具
	/// </summary>
	public const int Vehicle=6;
	
	/// <summary>
	/// 场景特效
	/// </summary>
	public const int SceneEffect=7;
	
	/// <summary>
	/// 操作体(不可被攻击)
	/// </summary>
	public const int Operation=8;
	
	/// <summary>
	/// 掉落物品
	/// </summary>
	public const int FieldItem=9;
	
	/// <summary>
	/// 建筑物
	/// </summary>
	public const int Building=10;
	
	/// <summary>
	/// 掉落物品包
	/// </summary>
	public const int FieldItemBag=11;
	
	/// <summary>
	/// 长度
	/// </summary>
	public static int size=12;
	
}
