using ShineEngine;

/// <summary>
/// 成就完成数据(generated by shine)
/// </summary>
public class AchievementCompleteData:BaseData
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=BaseDataType.AchievementComplete;
	
	/// <summary>
	/// id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 完成时间戳
	/// </summary>
	public long time;
	
	/// <summary>
	/// 是否领过奖励
	/// </summary>
	public bool hasGotReward;
	
	public AchievementCompleteData()
	{
		_dataID=BaseDataType.AchievementComplete;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "AchievementCompleteData";
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		this.id=stream.readInt();
		
		this.time=stream.readLong();
		
		this.hasGotReward=stream.readBoolean();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeInt(this.id);
		
		stream.writeLong(this.time);
		
		stream.writeBoolean(this.hasGotReward);
		
	}
	
	/// <summary>
	/// 复制(潜拷贝)
	/// </summary>
	protected override void toShadowCopy(BaseData data)
	{
		if(!(data is AchievementCompleteData))
			return;
		
		AchievementCompleteData mData=(AchievementCompleteData)data;
		
		this.id=mData.id;
		this.time=mData.time;
		this.hasGotReward=mData.hasGotReward;
	}
	
	/// <summary>
	/// 复制(深拷贝)
	/// </summary>
	protected override void toCopy(BaseData data)
	{
		if(!(data is AchievementCompleteData))
			return;
		
		AchievementCompleteData mData=(AchievementCompleteData)data;
		
		this.id=mData.id;
		
		this.time=mData.time;
		
		this.hasGotReward=mData.hasGotReward;
		
	}
	
	/// <summary>
	/// 是否数据一致
	/// </summary>
	protected override bool toDataEquals(BaseData data)
	{
		AchievementCompleteData mData=(AchievementCompleteData)data;
		if(this.id!=mData.id)
			return false;
		
		if(this.time!=mData.time)
			return false;
		
		if(this.hasGotReward!=mData.hasGotReward)
			return false;
		
		return true;
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.Append("id");
		writer.sb.Append(':');
		writer.sb.Append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("time");
		writer.sb.Append(':');
		writer.sb.Append(this.time);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("hasGotReward");
		writer.sb.Append(':');
		writer.sb.Append(this.hasGotReward);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 初始化初值
	/// </summary>
	public override void initDefault()
	{
		
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.id=stream.readInt();
		
		this.time=stream.readLong();
		
		this.hasGotReward=stream.readBoolean();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeInt(this.id);
		
		stream.writeLong(this.time);
		
		stream.writeBoolean(this.hasGotReward);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		this.id=0;
		this.time=0L;
		this.hasGotReward=false;
	}
	
}
