using ShineEngine;

/// <summary>
/// 任务完成数据(周期类用)(generated by shine)
/// </summary>
public class QuestCompleteData:BaseData
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=BaseDataType.QuestComplete;
	
	/// <summary>
	/// 恢复时间戳
	/// </summary>
	public long reTime;
	
	/// <summary>
	/// 任务ID
	/// </summary>
	public int id;
	
	/// <summary>
	/// 是否成功完成(否则是失败)
	/// </summary>
	public bool isSuccess;
	
	public QuestCompleteData()
	{
		_dataID=BaseDataType.QuestComplete;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "QuestCompleteData";
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		this.id=stream.readInt();
		
		this.reTime=stream.readLong();
		
		this.isSuccess=stream.readBoolean();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeInt(this.id);
		
		stream.writeLong(this.reTime);
		
		stream.writeBoolean(this.isSuccess);
		
	}
	
	/// <summary>
	/// 复制(潜拷贝)
	/// </summary>
	protected override void toShadowCopy(BaseData data)
	{
		if(!(data is QuestCompleteData))
			return;
		
		QuestCompleteData mData=(QuestCompleteData)data;
		
		this.id=mData.id;
		this.reTime=mData.reTime;
		this.isSuccess=mData.isSuccess;
	}
	
	/// <summary>
	/// 复制(深拷贝)
	/// </summary>
	protected override void toCopy(BaseData data)
	{
		if(!(data is QuestCompleteData))
			return;
		
		QuestCompleteData mData=(QuestCompleteData)data;
		
		this.id=mData.id;
		
		this.reTime=mData.reTime;
		
		this.isSuccess=mData.isSuccess;
		
	}
	
	/// <summary>
	/// 是否数据一致
	/// </summary>
	protected override bool toDataEquals(BaseData data)
	{
		QuestCompleteData mData=(QuestCompleteData)data;
		if(this.id!=mData.id)
			return false;
		
		if(this.reTime!=mData.reTime)
			return false;
		
		if(this.isSuccess!=mData.isSuccess)
			return false;
		
		return true;
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.Append("id");
		writer.sb.Append(':');
		writer.sb.Append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("reTime");
		writer.sb.Append(':');
		writer.sb.Append(this.reTime);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("isSuccess");
		writer.sb.Append(':');
		writer.sb.Append(this.isSuccess);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 初始化初值
	/// </summary>
	public override void initDefault()
	{
		
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.id=stream.readInt();
		
		this.reTime=stream.readLong();
		
		this.isSuccess=stream.readBoolean();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeInt(this.id);
		
		stream.writeLong(this.reTime);
		
		stream.writeBoolean(this.isSuccess);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		this.id=0;
		this.reTime=0L;
		this.isSuccess=false;
	}
	
}
