using ShineEngine;

/// <summary>
/// 角色使用数据(generated by shine)
/// </summary>
public class CharacterUseData:MUnitUseData
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=BaseDataType.CharacterUse;
	
	public CharacterUseData()
	{
		_dataID=BaseDataType.CharacterUse;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "CharacterUseData";
	}
	
	public override int getFightUnitID()
	{
		return CharacterConfig.get(id).fightUnitID;
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.endWriteObj();
	}
	
	public override int getUnitType()
	{
		return UnitType.Character;
	}
	
}
