using ShineEngine;

/// <summary>
/// 主单位(可脱离场景存在的)使用数据(generated by shine)
/// </summary>
public class MUnitUseData:BaseData
{
	/// <summary>
	/// 单位id
	/// </summary>
	public int id;
	
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=BaseDataType.MUnitUse;
	
	/// <summary>
	/// 等级
	/// </summary>
	public int level;
	
	/// <summary>
	/// 造型数据
	/// </summary>
	public UnitAvatarData avatar;
	
	/// <summary>
	/// 装备组
	/// </summary>
	public EquipContainerData equips;
	
	/// <summary>
	/// 战斗数据
	/// </summary>
	public UnitFightData fight;
	
	/// <summary>
	/// 是否上阵
	/// </summary>
	public bool isWorking;
	
	/// <summary>
	/// 主单位序号
	/// </summary>
	public int mIndex;
	
	public MUnitUseData()
	{
		_dataID=BaseDataType.MUnitUse;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "MUnitUseData";
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		this.id=stream.readInt();
		
		this.level=stream.readInt();
		
		this.avatar=(UnitAvatarData)stream.readDataSimpleNotNull();
		
		this.fight=(UnitFightData)stream.readDataSimpleNotNull();
		
		this.equips=(EquipContainerData)stream.readDataSimpleNotNull();
		
		this.mIndex=stream.readInt();
		
		this.isWorking=stream.readBoolean();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeInt(this.id);
		
		stream.writeInt(this.level);
		
		if(this.avatar!=null)
		{
			stream.writeDataSimpleNotNull(this.avatar);
		}
		else
		{
			nullObjError("avatar");
		}
		
		if(this.fight!=null)
		{
			stream.writeDataSimpleNotNull(this.fight);
		}
		else
		{
			nullObjError("fight");
		}
		
		if(this.equips!=null)
		{
			stream.writeDataSimpleNotNull(this.equips);
		}
		else
		{
			nullObjError("equips");
		}
		
		stream.writeInt(this.mIndex);
		
		stream.writeBoolean(this.isWorking);
		
	}
	
	/// <summary>
	/// 复制(潜拷贝)
	/// </summary>
	protected override void toShadowCopy(BaseData data)
	{
		if(!(data is MUnitUseData))
			return;
		
		MUnitUseData mData=(MUnitUseData)data;
		
		this.id=mData.id;
		this.level=mData.level;
		this.avatar=mData.avatar;
		this.fight=mData.fight;
		this.equips=mData.equips;
		this.mIndex=mData.mIndex;
		this.isWorking=mData.isWorking;
	}
	
	/// <summary>
	/// 复制(深拷贝)
	/// </summary>
	protected override void toCopy(BaseData data)
	{
		if(!(data is MUnitUseData))
			return;
		
		MUnitUseData mData=(MUnitUseData)data;
		
		this.id=mData.id;
		
		this.level=mData.level;
		
		if(mData.avatar!=null)
		{
			this.avatar=(UnitAvatarData)mData.avatar.clone();
		}
		else
		{
			this.avatar=null;
			nullObjError("avatar");
		}
		
		if(mData.fight!=null)
		{
			this.fight=(UnitFightData)mData.fight.clone();
		}
		else
		{
			this.fight=null;
			nullObjError("fight");
		}
		
		if(mData.equips!=null)
		{
			this.equips=(EquipContainerData)mData.equips.clone();
		}
		else
		{
			this.equips=null;
			nullObjError("equips");
		}
		
		this.mIndex=mData.mIndex;
		
		this.isWorking=mData.isWorking;
		
	}
	
	/// <summary>
	/// 是否数据一致
	/// </summary>
	protected override bool toDataEquals(BaseData data)
	{
		MUnitUseData mData=(MUnitUseData)data;
		if(this.id!=mData.id)
			return false;
		
		if(this.level!=mData.level)
			return false;
		
		if(mData.avatar!=null)
		{
			if(this.avatar==null)
				return false;
			if(!this.avatar.dataEquals(mData.avatar))
				return false;
		}
		else
		{
			if(this.avatar!=null)
				return false;
		}
		
		if(mData.fight!=null)
		{
			if(this.fight==null)
				return false;
			if(!this.fight.dataEquals(mData.fight))
				return false;
		}
		else
		{
			if(this.fight!=null)
				return false;
		}
		
		if(mData.equips!=null)
		{
			if(this.equips==null)
				return false;
			if(!this.equips.dataEquals(mData.equips))
				return false;
		}
		else
		{
			if(this.equips!=null)
				return false;
		}
		
		if(this.mIndex!=mData.mIndex)
			return false;
		
		if(this.isWorking!=mData.isWorking)
			return false;
		
		return true;
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.Append("id");
		writer.sb.Append(':');
		writer.sb.Append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("level");
		writer.sb.Append(':');
		writer.sb.Append(this.level);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("avatar");
		writer.sb.Append(':');
		if(this.avatar!=null)
		{
			this.avatar.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("UnitAvatarData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("fight");
		writer.sb.Append(':');
		if(this.fight!=null)
		{
			this.fight.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("UnitFightData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("equips");
		writer.sb.Append(':');
		if(this.equips!=null)
		{
			this.equips.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("EquipContainerData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("mIndex");
		writer.sb.Append(':');
		writer.sb.Append(this.mIndex);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("isWorking");
		writer.sb.Append(':');
		writer.sb.Append(this.isWorking);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 初始化初值
	/// </summary>
	public override void initDefault()
	{
		this.avatar=new UnitAvatarData();
		this.avatar.initDefault();
		this.fight=new UnitFightData();
		this.fight.initDefault();
		this.equips=new EquipContainerData();
		this.equips.initDefault();
	}
	
	/// <summary>
	/// 获取战斗单位ID
	/// </summary>
	public virtual int getFightUnitID()
	{
		return -1;
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.id=stream.readInt();
		
		this.level=stream.readInt();
		
		BaseData avatarT=stream.readDataFullNotNull();
		if(avatarT!=null)
		{
			if(avatarT is UnitAvatarData)
			{
				this.avatar=(UnitAvatarData)avatarT;
			}
			else
			{
				this.avatar=new UnitAvatarData();
				if(!(avatarT.GetType().IsAssignableFrom(typeof(UnitAvatarData))))
				{
					stream.throwTypeReadError(typeof(UnitAvatarData),avatarT.GetType());
				}
				this.avatar.shadowCopy(avatarT);
			}
		}
		else
		{
			this.avatar=null;
		}
		
		BaseData fightT=stream.readDataFullNotNull();
		if(fightT!=null)
		{
			if(fightT is UnitFightData)
			{
				this.fight=(UnitFightData)fightT;
			}
			else
			{
				this.fight=new UnitFightData();
				if(!(fightT.GetType().IsAssignableFrom(typeof(UnitFightData))))
				{
					stream.throwTypeReadError(typeof(UnitFightData),fightT.GetType());
				}
				this.fight.shadowCopy(fightT);
			}
		}
		else
		{
			this.fight=null;
		}
		
		BaseData equipsT=stream.readDataFullNotNull();
		if(equipsT!=null)
		{
			if(equipsT is EquipContainerData)
			{
				this.equips=(EquipContainerData)equipsT;
			}
			else
			{
				this.equips=new EquipContainerData();
				if(!(equipsT.GetType().IsAssignableFrom(typeof(EquipContainerData))))
				{
					stream.throwTypeReadError(typeof(EquipContainerData),equipsT.GetType());
				}
				this.equips.shadowCopy(equipsT);
			}
		}
		else
		{
			this.equips=null;
		}
		
		this.mIndex=stream.readInt();
		
		this.isWorking=stream.readBoolean();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.level);
		
		if(this.avatar!=null)
		{
			stream.writeDataFullNotNull(this.avatar);
		}
		else
		{
			nullObjError("avatar");
		}
		
		if(this.fight!=null)
		{
			stream.writeDataFullNotNull(this.fight);
		}
		else
		{
			nullObjError("fight");
		}
		
		if(this.equips!=null)
		{
			stream.writeDataFullNotNull(this.equips);
		}
		else
		{
			nullObjError("equips");
		}
		
		stream.writeInt(this.mIndex);
		
		stream.writeBoolean(this.isWorking);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		this.id=0;
		this.level=0;
		this.avatar=null;
		this.fight=null;
		this.equips=null;
		this.mIndex=0;
		this.isWorking=false;
	}
	
	public virtual int getUnitType()
	{
		Ctrl.throwError("this function should be override");
		return -1;
	}
	
}
