using ShineEngine;

/// <summary>
/// 位置朝向数据(generated by shine)
/// </summary>
public class PosDirData:BaseData
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=BaseDataType.PosDir;
	
	/// <summary>
	/// 位置
	/// </summary>
	public PosData pos;
	
	/// <summary>
	/// 朝向
	/// </summary>
	public DirData dir;
	
	public PosDirData()
	{
		_dataID=BaseDataType.PosDir;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "PosDirData";
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		this.pos=(PosData)stream.createData(PosData.dataID);
		this.pos.readBytesSimple(stream);
		
		this.dir=(DirData)stream.createData(DirData.dataID);
		this.dir.readBytesSimple(stream);
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		if(this.pos!=null)
		{
			this.pos.writeBytesSimple(stream);
		}
		else
		{
			nullObjError("pos");
		}
		
		if(this.dir!=null)
		{
			this.dir.writeBytesSimple(stream);
		}
		else
		{
			nullObjError("dir");
		}
		
	}
	
	/// <summary>
	/// 复制(潜拷贝)
	/// </summary>
	protected override void toShadowCopy(BaseData data)
	{
		if(!(data is PosDirData))
			return;
		
		PosDirData mData=(PosDirData)data;
		
		this.pos=mData.pos;
		this.dir=mData.dir;
	}
	
	/// <summary>
	/// 复制(深拷贝)
	/// </summary>
	protected override void toCopy(BaseData data)
	{
		if(!(data is PosDirData))
			return;
		
		PosDirData mData=(PosDirData)data;
		
		if(mData.pos!=null)
		{
			this.pos=(PosData)BytesControl.createData(PosData.dataID);
			this.pos.copy(mData.pos);
		}
		else
		{
			this.pos=null;
			nullObjError("pos");
		}
		
		if(mData.dir!=null)
		{
			this.dir=(DirData)BytesControl.createData(DirData.dataID);
			this.dir.copy(mData.dir);
		}
		else
		{
			this.dir=null;
			nullObjError("dir");
		}
		
	}
	
	/// <summary>
	/// 是否数据一致
	/// </summary>
	protected override bool toDataEquals(BaseData data)
	{
		PosDirData mData=(PosDirData)data;
		if(mData.pos!=null)
		{
			if(this.pos==null)
				return false;
			if(!this.pos.dataEquals(mData.pos))
				return false;
		}
		else
		{
			if(this.pos!=null)
				return false;
		}
		
		if(mData.dir!=null)
		{
			if(this.dir==null)
				return false;
			if(!this.dir.dataEquals(mData.dir))
				return false;
		}
		else
		{
			if(this.dir!=null)
				return false;
		}
		
		return true;
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.Append("pos");
		writer.sb.Append(':');
		if(this.pos!=null)
		{
			this.pos.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("PosData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("dir");
		writer.sb.Append(':');
		if(this.dir!=null)
		{
			this.dir.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("DirData=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 初始化初值
	/// </summary>
	public override void initDefault()
	{
		this.pos=new PosData();
		this.pos.initDefault();
		this.dir=new DirData();
		this.dir.initDefault();
	}
	
	/// <summary>
	/// 拷贝位置
	/// </summary>
	public void copyPos(PosData pos,DirData dir)
	{
		this.pos.copyPos(pos);
		this.dir.copyDir(dir);
	}
	
	public void copyPosDir(PosDirData posDir)
	{
		this.pos.copyPos(posDir.pos);
		this.dir.copyDir(posDir.dir);
	}
	
	/// <summary>
	/// 拷贝位置和朝向
	/// </summary>
	public void setByFArr(float[] arr)
	{
		pos.setByFArr(arr);

		dir.direction=arr.Length>3 ? arr[3] : 0f;
		dir.directionX=arr.Length>4 ? arr[4] : 0f;
	}
	
	/// <summary>
	/// 写到float[](编辑器用)
	/// </summary>
	public float[] getFArr()
	{
		float[] re=new float[5];
		writeFArr(re);
		return re;
	}
	
	/// <summary>
	/// 写到float[](编辑器用)
	/// </summary>
	public void writeFArr(float[] arr)
	{
		if(arr.Length<5)
			return;

		pos.writeFArr(arr);

		arr[3]=dir.direction;
		arr[4]=dir.directionX;
	}
	
	/// <summary>
	/// 回归0
	/// </summary>
	public override void clear()
	{
		pos.clear();
		dir.clear();
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.pos=(PosData)stream.createData(PosData.dataID);
		this.pos.readBytesFull(stream);
		
		this.dir=(DirData)stream.createData(DirData.dataID);
		this.dir.readBytesFull(stream);
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		if(this.pos!=null)
		{
			this.pos.writeBytesFull(stream);
		}
		else
		{
			nullObjError("pos");
		}
		
		if(this.dir!=null)
		{
			this.dir.writeBytesFull(stream);
		}
		else
		{
			nullObjError("dir");
		}
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		this.pos=null;
		this.dir=null;
	}
	
}
