using ShineEngine;

/// <summary>
/// 掉落包身份数据(generated by shine)
/// </summary>
public class FieldItemBagIdentityData:UnitIdentityData
{
	/// <summary>
	/// 来源怪物id
	/// </summary>
	public int fromMonsterID;
	
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=BaseDataType.FieldItemBagIdentity;
	
	public FieldItemBagIdentityData()
	{
		_dataID=BaseDataType.FieldItemBagIdentity;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FieldItemBagIdentityData";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.fromMonsterID=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.fromMonsterID);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.fromMonsterID=stream.readInt();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.fromMonsterID);
		
	}
	
	/// <summary>
	/// 复制(潜拷贝)
	/// </summary>
	protected override void toShadowCopy(BaseData data)
	{
		base.toShadowCopy(data);
		
		if(!(data is FieldItemBagIdentityData))
			return;
		
		FieldItemBagIdentityData mData=(FieldItemBagIdentityData)data;
		
		this.fromMonsterID=mData.fromMonsterID;
	}
	
	/// <summary>
	/// 复制(深拷贝)
	/// </summary>
	protected override void toCopy(BaseData data)
	{
		base.toCopy(data);
		
		if(!(data is FieldItemBagIdentityData))
			return;
		
		FieldItemBagIdentityData mData=(FieldItemBagIdentityData)data;
		
		this.fromMonsterID=mData.fromMonsterID;
		
	}
	
	/// <summary>
	/// 是否数据一致
	/// </summary>
	protected override bool toDataEquals(BaseData data)
	{
		if(!base.toDataEquals(data))
			return false;
		
		FieldItemBagIdentityData mData=(FieldItemBagIdentityData)data;
		if(this.fromMonsterID!=mData.fromMonsterID)
			return false;
		
		return true;
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("fromMonsterID");
		writer.sb.Append(':');
		writer.sb.Append(this.fromMonsterID);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 初始化初值
	/// </summary>
	public override void initDefault()
	{
		base.initDefault();
		
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.fromMonsterID=0;
	}
	
}
