using ShineEngine;

/// <summary>
/// 存库数据版本(generated by shine)
/// </summary>
public class SaveVersionData:BaseData
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=BaseDataType.SaveVersion;
	
	/// <summary>
	/// c层数据版本号
	/// </summary>
	public int cVersion;
	
	/// <summary>
	/// g层版本号(数据)
	/// </summary>
	public int gVersion;
	
	public SaveVersionData()
	{
		_dataID=BaseDataType.SaveVersion;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "SaveVersionData";
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		this.cVersion=stream.readInt();
		
		this.gVersion=stream.readInt();
		
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		stream.writeInt(this.cVersion);
		
		stream.writeInt(this.gVersion);
		
	}
	
	/// <summary>
	/// 复制(潜拷贝)
	/// </summary>
	protected override void toShadowCopy(BaseData data)
	{
		if(!(data is SaveVersionData))
			return;
		
		SaveVersionData mData=(SaveVersionData)data;
		
		this.cVersion=mData.cVersion;
		this.gVersion=mData.gVersion;
	}
	
	/// <summary>
	/// 复制(深拷贝)
	/// </summary>
	protected override void toCopy(BaseData data)
	{
		if(!(data is SaveVersionData))
			return;
		
		SaveVersionData mData=(SaveVersionData)data;
		
		this.cVersion=mData.cVersion;
		
		this.gVersion=mData.gVersion;
		
	}
	
	/// <summary>
	/// 是否数据一致
	/// </summary>
	protected override bool toDataEquals(BaseData data)
	{
		SaveVersionData mData=(SaveVersionData)data;
		if(this.cVersion!=mData.cVersion)
			return false;
		
		if(this.gVersion!=mData.gVersion)
			return false;
		
		return true;
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		writer.writeTabs();
		writer.sb.Append("cVersion");
		writer.sb.Append(':');
		writer.sb.Append(this.cVersion);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("gVersion");
		writer.sb.Append(':');
		writer.sb.Append(this.gVersion);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 初始化初值
	/// </summary>
	public override void initDefault()
	{
		
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		stream.startReadObj();
		
		this.cVersion=stream.readInt();
		
		this.gVersion=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		stream.startWriteObj();
		
		stream.writeInt(this.cVersion);
		
		stream.writeInt(this.gVersion);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		this.cVersion=0;
		this.gVersion=0;
	}
	
}
