using ShineEngine;

/// <summary>
/// 客户端获取版本消息(generated by shine)
/// </summary>
public class ClientGetVersionHttpRequest:LoginHttpRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=LoginHttpRequestType.ClientGetVersion;
	
	/// <summary>
	/// 当前资源版本号(用来做url重定向)
	/// </summary>
	public int resourceVersion;
	
	/// <summary>
	/// 返回值
	/// </summary>
	protected ClientGetVersionResult _re;
	
	/// <summary>
	/// 客户端平台类型(见ClientPlatformType)
	/// </summary>
	public int platformType;
	
	public ClientGetVersionHttpRequest()
	{
		_dataID=LoginHttpRequestType.ClientGetVersion;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void toRead()
	{
		_re=new ClientGetVersionResult();
		readResult(_re,_resultStream);
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void onComplete()
	{
		Ctrl.print("getVersionUrl:",_url);
		
		if(_result==-1 || _re==null)
		{
			Ctrl.print("clientGetVersion失败");
			GameC.mainLogin.onGetVersionFailed();
			return;
		}

		GameC.mainLogin.onGetVersionSuccess(_re.version,_re.redirectURL);
	}
	
	/// <summary>
	/// 获取结果
	/// </summary>
	public ClientGetVersionResult getResult()
	{
		return _re;
	}
	
	/// <summary>
	/// 同步执行
	/// </summary>
	public ClientGetVersionResult sendMSync()
	{
		doSendSync();
		return _re;
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.platformType);
		
		stream.writeInt(this.resourceVersion);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.platformType);
		
		stream.writeInt(this.resourceVersion);
		
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static ClientGetVersionHttpRequest create(int platformType,int resourceVersion)
	{
		ClientGetVersionHttpRequest re=new ClientGetVersionHttpRequest();
		re.platformType=platformType;
		re.resourceVersion=resourceVersion;
		return re;
	}
	
}
