using ShineEngine;

/// <summary>
/// 取消上架拍卖行物品消息(generated by shine)
/// </summary>
public class FuncAuctionCancelSellItemRequest:FuncRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.FuncAuctionCancelSellItem;
	
	/// <summary>
	/// 实例id
	/// </summary>
	public long instanceID;
	
	public FuncAuctionCancelSellItemRequest()
	{
		_dataID=GameRequestType.FuncAuctionCancelSellItem;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncAuctionCancelSellItemRequest";
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeLong(this.instanceID);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeLong(this.instanceID);
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("instanceID");
		writer.sb.Append(':');
		writer.sb.Append(this.instanceID);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.instanceID=0L;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static FuncAuctionCancelSellItemRequest create(int funcID,long instanceID)
	{
		FuncAuctionCancelSellItemRequest re=(FuncAuctionCancelSellItemRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.instanceID=instanceID;
		return re;
	}
	
}
