using ShineEngine;

/// <summary>
/// 获取拍卖行物品推荐价格(generated by shine)
/// </summary>
public class FuncGetAuctionItemSuggestPriceRequest:FuncRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.FuncGetAuctionItemSuggestPrice;
	
	public int itemID;
	
	public FuncGetAuctionItemSuggestPriceRequest()
	{
		_dataID=GameRequestType.FuncGetAuctionItemSuggestPrice;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncGetAuctionItemSuggestPriceRequest";
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.itemID);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.itemID);
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("itemID");
		writer.sb.Append(':');
		writer.sb.Append(this.itemID);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.itemID=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static FuncGetAuctionItemSuggestPriceRequest create(int funcID,int itemID)
	{
		FuncGetAuctionItemSuggestPriceRequest re=(FuncGetAuctionItemSuggestPriceRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.itemID=itemID;
		return re;
	}
	
}
