using ShineEngine;

/// <summary>
/// 移动物品消息(generated by shine)
/// </summary>
public class FuncMoveItemRequest:FuncRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.FuncMoveItem;
	
	/// <summary>
	/// 从某格子
	/// </summary>
	public int fromIndex;
	
	/// <summary>
	/// 到某格子
	/// </summary>
	public int toIndex;
	
	public FuncMoveItemRequest()
	{
		_dataID=GameRequestType.FuncMoveItem;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncMoveItemRequest";
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.fromIndex);
		
		stream.writeInt(this.toIndex);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.fromIndex);
		
		stream.writeInt(this.toIndex);
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("fromIndex");
		writer.sb.Append(':');
		writer.sb.Append(this.fromIndex);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("toIndex");
		writer.sb.Append(':');
		writer.sb.Append(this.toIndex);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.fromIndex=0;
		this.toIndex=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static FuncMoveItemRequest create(int funcID,int fromIndex,int toIndex)
	{
		FuncMoveItemRequest re=(FuncMoveItemRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.fromIndex=fromIndex;
		re.toIndex=toIndex;
		return re;
	}
	
}
