using ShineEngine;

/// <summary>
/// 穿戴装备(从背包)(generated by shine)
/// </summary>
public class FuncPutOnEquipRequest:FuncRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.FuncPutOnEquip;
	
	/// <summary>
	/// 背包序号
	/// </summary>
	public int bagIndex;
	
	/// <summary>
	/// 装备槽位
	/// </summary>
	public int slot;
	
	public FuncPutOnEquipRequest()
	{
		_dataID=GameRequestType.FuncPutOnEquip;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncPutOnEquipRequest";
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.slot);
		
		stream.writeInt(this.bagIndex);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.slot);
		
		stream.writeInt(this.bagIndex);
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("slot");
		writer.sb.Append(':');
		writer.sb.Append(this.slot);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("bagIndex");
		writer.sb.Append(':');
		writer.sb.Append(this.bagIndex);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.slot=0;
		this.bagIndex=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static FuncPutOnEquipRequest create(int funcID,int slot,int bagIndex)
	{
		FuncPutOnEquipRequest re=(FuncPutOnEquipRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.slot=slot;
		re.bagIndex=bagIndex;
		return re;
	}
	
}
