using ShineEngine;

/// <summary>
/// 获取每页数据(主线程)(generated by shine)
/// </summary>
public class FuncGetPageShowRequest:FuncRRequest
{
	/// <summary>
	/// 参数(默认0)
	/// </summary>
	public int arg;
	
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.FuncGetPageShow;
	
	/// <summary>
	/// 页码
	/// </summary>
	public int page;
	
	public FuncGetPageShowRequest()
	{
		_dataID=GameRequestType.FuncGetPageShow;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncGetPageShowRequest";
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("page");
		writer.sb.Append(':');
		writer.sb.Append(this.page);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("arg");
		writer.sb.Append(':');
		writer.sb.Append(this.arg);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.page);
		
		stream.writeInt(this.arg);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.page);
		
		stream.writeInt(this.arg);
		
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.page=0;
		this.arg=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static FuncGetPageShowRequest create(int funcID,int page,int arg)
	{
		FuncGetPageShowRequest re=(FuncGetPageShowRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.page=page;
		re.arg=arg;
		return re;
	}
	
}
