using ShineEngine;

/// <summary>
/// 设置职位(generated by shine)
/// </summary>
public class FuncSetTitleRoleGroupRequest:FuncPlayerRoleGroupRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.FuncSetTitleRoleGroup;
	
	/// <summary>
	/// 角色id
	/// </summary>
	public long playerID;
	
	/// <summary>
	/// 职位
	/// </summary>
	public int title;
	
	public FuncSetTitleRoleGroupRequest()
	{
		_dataID=GameRequestType.FuncSetTitleRoleGroup;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncSetTitleRoleGroupRequest";
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeLong(this.playerID);
		
		stream.writeInt(this.title);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
		stream.writeInt(this.title);
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("playerID");
		writer.sb.Append(':');
		writer.sb.Append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("title");
		writer.sb.Append(':');
		writer.sb.Append(this.title);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.playerID=0L;
		this.title=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static FuncSetTitleRoleGroupRequest create(int funcID,long groupID,long playerID,int title)
	{
		FuncSetTitleRoleGroupRequest re=(FuncSetTitleRoleGroupRequest)BytesControl.createRequest(dataID);
		re.funcID=funcID;
		re.groupID=groupID;
		re.playerID=playerID;
		re.title=title;
		return re;
	}
	
}
