using ShineEngine;

/// <summary>
/// 角色绑定平台消息(generated by shine)
/// </summary>
public class ApplyBindPlatformRequest:GameRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.ApplyBindPlatform;
	
	public string uid;
	
	public string platform;
	
	public ApplyBindPlatformRequest()
	{
		_dataID=GameRequestType.ApplyBindPlatform;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "ApplyBindPlatformRequest";
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("uid");
		writer.sb.Append(':');
		writer.sb.Append(this.uid);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("platform");
		writer.sb.Append(':');
		writer.sb.Append(this.platform);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeUTF(this.uid);
		
		stream.writeUTF(this.platform);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeUTF(this.uid);
		
		stream.writeUTF(this.platform);
		
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.uid="";
		this.platform="";
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static ApplyBindPlatformRequest create(string uid,string platform)
	{
		ApplyBindPlatformRequest re=(ApplyBindPlatformRequest)BytesControl.createRequest(dataID);
		re.uid=uid;
		re.platform=platform;
		return re;
	}
	
}
