using ShineEngine;

/// <summary>
/// 请求进入场景消息(generated by shine)
/// </summary>
public class ApplyEnterSceneRequest:GameRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.ApplyEnterScene;
	
	/// <summary>
	/// 场景ID
	/// </summary>
	public int id;
	
	/// <summary>
	/// 线ID
	/// </summary>
	public int line=-1;
	
	/// <summary>
	/// 位置ID
	/// </summary>
	public int posID=-1;
	
	public ApplyEnterSceneRequest()
	{
		_dataID=GameRequestType.ApplyEnterScene;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "ApplyEnterSceneRequest";
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("id");
		writer.sb.Append(':');
		writer.sb.Append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("line");
		writer.sb.Append(':');
		writer.sb.Append(this.line);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("posID");
		writer.sb.Append(':');
		writer.sb.Append(this.posID);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.line);
		
		stream.writeInt(this.posID);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.line);
		
		stream.writeInt(this.posID);
		
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.id=0;
		this.line=0;
		this.posID=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static ApplyEnterSceneRequest create(int id,int line,int posID)
	{
		ApplyEnterSceneRequest re=(ApplyEnterSceneRequest)BytesControl.createRequest(dataID);
		re.id=id;
		re.line=line;
		re.posID=posID;
		return re;
	}
	
}
