using ShineEngine;

/// <summary>
/// 单位骑乘载具(generated by shine)
/// </summary>
public class CUnitGetOnVehicleRequest:CUnitRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.CUnitGetOnVehicle;
	
	/// <summary>
	/// 骑乘位置
	/// </summary>
	public int index;
	
	/// <summary>
	/// 载具实例id
	/// </summary>
	public int vehicleInstanceID;
	
	public CUnitGetOnVehicleRequest()
	{
		_dataID=GameRequestType.CUnitGetOnVehicle;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "CUnitGetOnVehicleRequest";
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.vehicleInstanceID);
		
		stream.writeInt(this.index);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.vehicleInstanceID);
		
		stream.writeInt(this.index);
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("vehicleInstanceID");
		writer.sb.Append(':');
		writer.sb.Append(this.vehicleInstanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("index");
		writer.sb.Append(':');
		writer.sb.Append(this.index);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.vehicleInstanceID=0;
		this.index=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static CUnitGetOnVehicleRequest create(int instanceID,int vehicleInstanceID,int index)
	{
		CUnitGetOnVehicleRequest re=(CUnitGetOnVehicleRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.vehicleInstanceID=vehicleInstanceID;
		re.index=index;
		return re;
	}
	
}
