using ShineEngine;

/// <summary>
/// 单位操作消息(generated by shine)
/// </summary>
public class CUnitOperateRequest:CUnitRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.CUnitOperate;
	
	/// <summary>
	/// 目标操作体实例ID
	/// </summary>
	public int targetInstanceID;
	
	/// <summary>
	/// 参数
	/// </summary>
	public int arg;
	
	public CUnitOperateRequest()
	{
		_dataID=GameRequestType.CUnitOperate;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "CUnitOperateRequest";
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.targetInstanceID);
		
		stream.writeInt(this.arg);
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.targetInstanceID);
		
		stream.writeInt(this.arg);
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("targetInstanceID");
		writer.sb.Append(':');
		writer.sb.Append(this.targetInstanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("arg");
		writer.sb.Append(':');
		writer.sb.Append(this.arg);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.targetInstanceID=0;
		this.arg=0;
	}
	
	protected override void copyData()
	{
		base.copyData();
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static CUnitOperateRequest create(int instanceID,int targetInstanceID,int arg)
	{
		CUnitOperateRequest re=(CUnitOperateRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.targetInstanceID=targetInstanceID;
		re.arg=arg;
		return re;
	}
	
}
