using ShineEngine;

/// <summary>
/// 控制单位特殊移动(generated by shine)
/// </summary>
public class CUnitSpecialMoveRequest:CUnitRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.CUnitSpecialMove;
	
	/// <summary>
	/// 特殊移动ID
	/// </summary>
	public int id;
	
	/// <summary>
	/// 当前位置
	/// </summary>
	public PosDirData posDir;
	
	/// <summary>
	/// 参数组
	/// </summary>
	public int[] args;
	
	public CUnitSpecialMoveRequest()
	{
		_dataID=GameRequestType.CUnitSpecialMove;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "CUnitSpecialMoveRequest";
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("id");
		writer.sb.Append(':');
		writer.sb.Append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("posDir");
		writer.sb.Append(':');
		if(this.posDir!=null)
		{
			this.posDir.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("PosDirData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("args");
		writer.sb.Append(':');
		writer.sb.Append("Array<int>");
		if(this.args!=null)
		{
			int[] argsT=this.args;
			int argsLen=argsT.Length;
			writer.sb.Append('(');
			writer.sb.Append(argsLen);
			writer.sb.Append(')');
			writer.writeEnter();
			writer.writeLeftBrace();
			for(int argsI=0;argsI<argsLen;++argsI)
			{
				int argsV=argsT[argsI];
				writer.writeTabs();
				writer.sb.Append(argsI);
				writer.sb.Append(':');
				writer.sb.Append(argsV);
				
				writer.writeEnter();
			}
			writer.writeRightBrace();
		}
		else
		{
			writer.sb.Append("=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.id);
		
		if(this.posDir!=null)
		{
			stream.writeBoolean(true);
			this.posDir.writeBytesFull(stream);
		}
		else
		{
			stream.writeBoolean(false);
		}
		
		if(this.args!=null)
		{
			stream.writeBoolean(true);
			int[] argsT=this.args;
			stream.writeLen(argsT.Length);
			for(int argsVI=0,argsVLen=argsT.Length;argsVI<argsVLen;++argsVI)
			{
				int argsV=argsT[argsVI];
				stream.writeInt(argsV);
				
			}
		}
		else
		{
			stream.writeBoolean(false);
		}
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		if(this.posDir!=null)
		{
			stream.writeBoolean(true);
			this.posDir.writeBytesSimple(stream);
		}
		else
		{
			stream.writeBoolean(false);
		}
		
		if(this.args!=null)
		{
			stream.writeBoolean(true);
			int[] argsT=this.args;
			stream.writeLen(argsT.Length);
			for(int argsVI=0,argsVLen=argsT.Length;argsVI<argsVLen;++argsVI)
			{
				int argsV=argsT[argsVI];
				stream.writeInt(argsV);
				
			}
		}
		else
		{
			stream.writeBoolean(false);
		}
		
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.id=0;
		if(this.posDir!=null)
		{
			this.posDir.release(pool);
			this.posDir=null;
		}
	}
	
	protected override void copyData()
	{
		base.copyData();
		PosDirData posDirTemp=posDir;
		if(posDirTemp!=null)
		{
			this.posDir=(PosDirData)BytesControl.createData(PosDirData.dataID);
			this.posDir.copy(posDirTemp);
		}
		else
		{
			this.posDir=null;
		}
		
		int[] argsTemp=args;
		if(argsTemp!=null)
		{
			int[] argsR=argsTemp;
			int argsLen=argsR.Length;
			this.args=new int[argsLen];
			BytesControl.arrayCopy(argsTemp,this.args,argsLen);
		}
		else
		{
			this.args=null;
		}
		
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static CUnitSpecialMoveRequest create(int instanceID,int id,PosDirData posDir,int[] args)
	{
		CUnitSpecialMoveRequest re=(CUnitSpecialMoveRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.id=id;
		re.posDir=posDir;
		re.args=args;
		return re;
	}
	
}
