using ShineEngine;

/// <summary>
/// 客户端单位停止移动消息(generated by shine)
/// </summary>
public class CUnitStopMoveRequest:CUnitRRequest
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameRequestType.CUnitStopMove;
	
	/// <summary>
	/// 停止的客户端位置
	/// </summary>
	public PosDirData posDir;
	
	public CUnitStopMoveRequest()
	{
		_dataID=GameRequestType.CUnitStopMove;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "CUnitStopMoveRequest";
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("posDir");
		writer.sb.Append(':');
		if(this.posDir!=null)
		{
			this.posDir.writeDataString(writer);
		}
		else
		{
			writer.sb.Append("PosDirData=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 写入字节流(完整版)
	/// </summary>
	protected override void toWriteBytesFull(BytesWriteStream stream)
	{
		base.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.posDir!=null)
		{
			this.posDir.writeBytesFull(stream);
		}
		else
		{
			nullObjError("posDir");
		}
		
		stream.endWriteObj();
	}
	
	/// <summary>
	/// 写入字节流(简版)
	/// </summary>
	protected override void toWriteBytesSimple(BytesWriteStream stream)
	{
		base.toWriteBytesSimple(stream);
		
		if(this.posDir!=null)
		{
			this.posDir.writeBytesSimple(stream);
		}
		else
		{
			nullObjError("posDir");
		}
		
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.posDir.release(pool);
		this.posDir=null;
	}
	
	protected override void copyData()
	{
		base.copyData();
		PosDirData posDirTemp=posDir;
		if(posDirTemp!=null)
		{
			this.posDir=(PosDirData)BytesControl.createData(PosDirData.dataID);
			this.posDir.copy(posDirTemp);
		}
		else
		{
			this.posDir=null;
			nullObjError("posDir");
		}
		
	}
	
	/// <summary>
	/// 创建实例
	/// </summary>
	public static CUnitStopMoveRequest create(int instanceID,PosDirData posDir)
	{
		CUnitStopMoveRequest re=(CUnitStopMoveRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.posDir=posDir;
		return re;
	}
	
}
