using ShineEngine;

/// <summary>
/// 活动重置(generated by shine)
/// </summary>
public class ActivityResetResponse:GameResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.ActivityReset;
	
	public int id;
	
	/// <summary>
	/// 是否到时间自然触发
	/// </summary>
	public bool atTime;
	
	/// <summary>
	/// 下个重置时间
	/// </summary>
	public long nextTime;
	
	public ActivityResetResponse()
	{
		_dataID=GameResponseType.ActivityReset;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.activity.onActivityResetByServer(id,nextTime,atTime);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "ActivityResetResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.id=stream.readInt();
		
		this.nextTime=stream.readLong();
		
		this.atTime=stream.readBoolean();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.nextTime=stream.readLong();
		
		this.atTime=stream.readBoolean();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("id");
		writer.sb.Append(':');
		writer.sb.Append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("nextTime");
		writer.sb.Append(':');
		writer.sb.Append(this.nextTime);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("atTime");
		writer.sb.Append(':');
		writer.sb.Append(this.atTime);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.id=0;
		this.nextTime=0L;
		this.atTime=false;
	}
	
}
