using ShineEngine;

/// <summary>
/// 回复获取拍卖行物品推荐价格(generated by shine)
/// </summary>
public class FuncReGetAuctionItemSuggestPriceResponse:FuncSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.FuncReGetAuctionItemSuggestPrice;
	
	/// <summary>
	/// 物品id
	/// </summary>
	public int itemID;
	
	/// <summary>
	/// 单价
	/// </summary>
	public int price;
	
	public FuncReGetAuctionItemSuggestPriceResponse()
	{
		_dataID=GameResponseType.FuncReGetAuctionItemSuggestPrice;
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncReGetAuctionItemSuggestPriceResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.itemID=stream.readInt();
		
		this.price=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.itemID=stream.readInt();
		
		this.price=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("itemID");
		writer.sb.Append(':');
		writer.sb.Append(this.itemID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("price");
		writer.sb.Append(':');
		writer.sb.Append(this.price);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.itemID=0;
		this.price=0;
	}
	
}
