using ShineEngine;

/// <summary>
/// 添加单个物品数目消息(generated by shine)
/// </summary>
public class FuncAddOneItemNumResponse:FuncSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.FuncAddOneItemNum;
	
	/// <summary>
	/// 途径
	/// </summary>
	public int way;
	
	/// <summary>
	/// 序号
	/// </summary>
	public int index;
	
	/// <summary>
	/// 数目
	/// </summary>
	public int num;
	
	public FuncAddOneItemNumResponse()
	{
		_dataID=GameResponseType.FuncAddOneItemNum;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.func.getBaseItemContainerTool(funcID).onAddItemNumByServer(index,num,way);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncAddOneItemNumResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.way=stream.readInt();
		
		this.index=stream.readInt();
		
		this.num=stream.readInt();
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.way=stream.readInt();
		
		this.index=stream.readInt();
		
		this.num=stream.readInt();
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("way");
		writer.sb.Append(':');
		writer.sb.Append(this.way);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("index");
		writer.sb.Append(':');
		writer.sb.Append(this.index);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("num");
		writer.sb.Append(':');
		writer.sb.Append(this.num);
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.way=0;
		this.index=0;
		this.num=0;
	}
	
}
