using ShineEngine;

/// <summary>
/// 移除物品消息(generated by shine)
/// </summary>
public class FuncRemoveItemResponse:FuncSResponse
{
	/// <summary>
	/// 数据类型ID
	/// </summary>
	public const int dataID=GameResponseType.FuncRemoveItem;
	
	/// <summary>
	/// 途径
	/// </summary>
	public int way;
	
	/// <summary>
	/// 移除序号数目组
	/// </summary>
	public IntIntMap dic;
	
	public FuncRemoveItemResponse()
	{
		_dataID=GameResponseType.FuncRemoveItem;
	}
	
	/// <summary>
	/// 执行
	/// </summary>
	protected override void execute()
	{
		me.func.getBaseItemContainerTool(funcID).onRemoveItemsByServer(dic,way);
	}
	
	/// <summary>
	/// 获取数据类名
	/// </summary>
	public override string getDataClassName()
	{
		return "FuncRemoveItemResponse";
	}
	
	/// <summary>
	/// 读取字节流(完整版)
	/// </summary>
	protected override void toReadBytesFull(BytesReadStream stream)
	{
		base.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.way=stream.readInt();
		
		int dicLen=stream.readLen();
		if(this.dic!=null)
		{
			this.dic.clear();
			this.dic.ensureCapacity(dicLen);
		}
		else
		{
			this.dic=new IntIntMap(dicLen);
		}
		
		IntIntMap dicT=this.dic;
		for(int dicI=dicLen-1;dicI>=0;--dicI)
		{
			int dicK;
			int dicV;
			dicK=stream.readInt();
			
			dicV=stream.readInt();
			
			dicT.put(dicK,dicV);
		}
		
		stream.endReadObj();
	}
	
	/// <summary>
	/// 读取字节流(简版)
	/// </summary>
	protected override void toReadBytesSimple(BytesReadStream stream)
	{
		base.toReadBytesSimple(stream);
		
		this.way=stream.readInt();
		
		int dicLen=stream.readLen();
		if(this.dic!=null)
		{
			this.dic.clear();
			this.dic.ensureCapacity(dicLen);
		}
		else
		{
			this.dic=new IntIntMap(dicLen);
		}
		
		IntIntMap dicT=this.dic;
		for(int dicI=dicLen-1;dicI>=0;--dicI)
		{
			int dicK;
			int dicV;
			dicK=stream.readInt();
			
			dicV=stream.readInt();
			
			dicT.put(dicK,dicV);
		}
		
	}
	
	/// <summary>
	/// 转文本输出
	/// </summary>
	protected override void toWriteDataString(DataWriter writer)
	{
		base.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.Append("way");
		writer.sb.Append(':');
		writer.sb.Append(this.way);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.Append("dic");
		writer.sb.Append(':');
		writer.sb.Append("Map<int,int>");
		if(this.dic!=null)
		{
			writer.sb.Append('(');
			writer.sb.Append(this.dic.size());
			writer.sb.Append(')');
			writer.writeEnter();
			writer.writeLeftBrace();
			if(!this.dic.isEmpty())
			{
				int dicKFreeValue=this.dic.getFreeValue();
				int[] dicKKeys=this.dic.getKeys();
				int[] dicVValues=this.dic.getValues();
				for(int dicKI=dicKKeys.Length-1;dicKI>=0;--dicKI)
				{
					int dicK=dicKKeys[dicKI];
					if(dicK!=dicKFreeValue)
					{
						int dicV=dicVValues[dicKI];
						writer.writeTabs();
						writer.sb.Append(dicK);
						
						writer.sb.Append(':');
						writer.sb.Append(dicV);
						
						writer.writeEnter();
					}
				}
			}
			writer.writeRightBrace();
		}
		else
		{
			writer.sb.Append("=null");
		}
		
		writer.writeEnter();
	}
	
	/// <summary>
	/// 回池
	/// </summary>
	protected override void toRelease(DataPool pool)
	{
		base.toRelease(pool);
		
		this.way=0;
		this.dic=null;
	}
	
}
